<script setup>

import { ref, onBeforeMount } from 'vue';

const iconSrach = '<svg class="absolute text-slate-400 h-5 w-5 stroke-violet-900 stroke-2 fill-emerald-600" viewBox="0 0 20 20" fill="currentColor"><path fill-rule="evenodd" d="M8 4a4 4 0 100 8 4 4 0 000-8zM2 8a6 6 0 1110.89 3.476l4.817 4.817a1 1 0 01-1.414 1.414l-4.816-4.816A6 6 0 012 8z" clip-rule="evenodd"></path></svg>'

function seeInput(){
console.log('Esto se ejecuta al momento de dar click en el input');
}

onBeforeMount(() => {
verify()
verificarLocalSotrage()
document.querySelector('#body').classList.add('overflow-hidden')
})

function verificarLocalSotrage(){
if("theme" in localStorage) return
else{
	if(window.matchMedia('(prefers-color-scheme: dark)').matches){
	localStorage.setItem('theme', 'dark')
	}else{
	localStorage.setItem('theme', 'light')
	}
}
}

function verify(){
if(localStorage.getItem('theme') === 'dark'){
	document.documentElement.classList.add('dark')
}else{
	document.documentElement.classList.remove('dark')
}
}
window.matchMedia('(prefers-color-scheme: dark)').addEventListener('change', verifyClass)

function verifyClass(value){
location.reload()
if(value.matches){
	localStorage.setItem('theme', 'dark')
}else{
	localStorage.setItem('theme', 'light')
}
}

function changeModeDark(){
if(localStorage.theme == 'dark'){
	localStorage.setItem('theme', 'light')
	verify()
}else{
	localStorage.setItem('theme', 'dark')
	verify()
}
}

const views = ref([
{ name: 'reaction', viewOne: 80, color: 'text-red-400', bg: 'bg-red-100' },
{ name: 'memory', viewOne: 80, color: 'text-yellow-400', bg: 'bg-yellow-100' },
{ name: 'verbal', viewOne: 80, color: 'text-green-400', bg: 'bg-green-100' },
{ name: 'Visual', viewOne: 80, color: 'text-blue-400', bg: 'bg-blue-100' },
])

</script>

